using System;
using System.Collections.Generic;

public class Graph
{
    private Dictionary<int, List<int>> _adjacencyList;

    public Graph()
    {
        _adjacencyList = new Dictionary<int, List<int>>();
    }

    public void AddVertex(int vertex)
    {
        if (!_adjacencyList.ContainsKey(vertex))
        {
            _adjacencyList[vertex] = new List<int>();
        }
    }

    public void AddEdge(int source, int destination)
    {
        if (!_adjacencyList.ContainsKey(source))
        {
            AddVertex(source);
        }

        if (!_adjacencyList.ContainsKey(destination))
        {
            AddVertex(destination);
        }

        _adjacencyList[source].Add(destination);
        _adjacencyList[destination].Add(source); // Undirected graph
    }

    public void PrintGraph()
    {
        foreach (var vertex in _adjacencyList.Keys)
        {
            Console.Write(vertex + " -> ");
            foreach (var neighbor in _adjacencyList[vertex])
            {
                Console.Write(neighbor + " ");
            }
            Console.WriteLine();
        }
    }
}

class Program
{
    static void Main(string[] args)
    {
        var graph = new Graph();
        graph.AddVertex(0);
        graph.AddVertex(1);
        graph.AddVertex(2);
        graph.AddEdge(0, 1);
        graph.AddEdge(1, 2);
        graph.AddEdge(2, 0);
        graph.PrintGraph();
    }
}
```

