public static boolean isBST(Node node, Node minValue, Node maxValue) {
    // Base case: empty tree or single node
    if (node == null) {
        return true;
    }

    // Recursive case: check left subtree, current node, and right subtree
    return (isBST(node.left, minValue, node) &&
            (minValue == null || node.data > minValue.data) &&
            (maxValue == null || node.data < maxValue.data) &&
            isBST(node.right, node, maxValue));
}

// Helper method to find min value in BST
public static Node minValue(Node node) {
    Node current = node;
    while (current.left != null) {
        current = current.left;
    }
    return current;
}

