function shoelaceArea(vertices) {
  let area = 0;
  const n = vertices.length;

  for (let i = 0; i < n; i++) {
    const j = (i + 1) % n;
    area += (vertices[i].x * vertices[j].y) - (vertices[j].x * vertices[i].y);
  }

  area = Math.abs(area / 2);
  return area;
}

